import React, {useEffect, useState} from "react";
import axios from "axios";
import { useLocation, useParams } from "react-router-dom";

const RECIPE_URL = "http://localhost:8000/recipe/recipes/";

function RecipeEdit() {
    let { id } = useParams();
    const recipeToEdit = useLocation().state.recipe;
    const currRecipeId = id;
    const[recipe, setRecipe] = useState();
    const [isLoading, setLoading] = useState(true);

    useEffect(() => {
        if(recipeToEdit) {
            setRecipe(recipeToEdit);
        } else {
            async function getRecipeInfo() {
                var url = `${RECIPE_URL}${currRecipeId}/`;
                const response = await (axios.get(url));
                setRecipe(response.data);
            };
            getRecipeInfo();
        }
        setLoading(false);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    let handleSubmit = (event) => {
        event.preventDefault();
        console.log(event);
        alert(JSON.stringify("Helloo  world"));
    }

    if (isLoading) {
        return <div className="App">Loading...</div>;
    }

    return(
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <h1>Edit recipe: {currRecipeId}</h1>
                </div>

                <label>Id: </label><label readOnly id='recipeId'>{currRecipeId}</label>
                <br/>

                <label>Name: </label>
                <input type='text' name='name' defaultValue={recipe.name} />
                <br/>

                <label>Ingredients: </label>
                {recipe.ingredients.map((item, index) => (
                    <input type='text' key="ingredient-${}" defaultValue={item.name} />
                ))}
                <br/>

                <br/>
                <button className="button submit" type="submit">Submit</button>
            </form>
        </div>
    );
}

export default RecipeEdit;
