import { useState } from "react";
export default initVal => {
    const [values, setValues] = useState(initVal);
    const handleChange = (i, e) => {
        let newValues = [...values];
        newValues[i]["name"] = e.target.value;
        setValues(newValues);
    }
    const addValue = () => {
        setValues([...values, {}])
    }
    const removeValue = (i) => {
        let newValues = [...values];
        newValues.splice(i, 1);
        setValues(newValues)
    }
    const reset = () => {
        setValues(initVal);
    }
    return [values, handleChange, addValue, removeValue, reset]
};
