import React, {useEffect, useState} from "react";
import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";

const RECIPE_URL = "http://localhost:8000/recipe/recipes/";

function RecipeEdit() {
    let { id } = useParams();
    const currState = useLocation().state;
    const currRecipeId = id;
    const[recipe, setRecipe] = useState();
    const [isLoading, setLoading] = useState(true);
    let navigate = useNavigate();
    const [formValues, setFormValues] = ({name:"", description:"", ingredients:[{}]});

    useEffect(() => {
        if(currState) {
            var recipeToEdit = currState.recipe;
            setRecipe(recipeToEdit);
            setFormValues(recipe);
            setLoading(false);
        } else {
            async function getRecipeInfo() {
                var url = `${RECIPE_URL}${currRecipeId}/`;
                const response = await (axios.get(url));
                setRecipe(response.data);
                setFormValues(recipe);
                setLoading(false);
            };
            getRecipeInfo();
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(formValues));
    }

    return(
        <form onSubmit={handleSubmit}>
