import React, {useEffect, useState} from "react";
import axios from "axios";
import { Route, useParams } from "react-router-dom";

const RECIPE_URL = "http://localhost:8000/recipe/recipes/";

function RecipeEdit() {
    let { id } = useParams(); 
    const currRecipeId = id;
    const[recipe, setRecipe] = useState();

    useEffect(() => {
        console.log("----- ----- ----- ----- ----- ----- ----- ");
        console.log(currRecipeId);
        console.log("----- ----- ----- ----- ----- ----- ----- ");

        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getRecipeInfo = () => {
        var url = `${RECIPE_URL}${currRecipeId}/`;
        axios.get(url).then((response) => {
            setRecipe(response.data);
        });
    };

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify("Helloo  world"));
    }

    return(
        <form onSubmit={handleSubmit}>
            <div>
                <h1>Edit recipe: {currRecipeId}</h1>
            </div>

            <label>Id: </label><label readOnly id='textId'>{currRecipeId}</label>
            <br/>

            <label>Name: </label>
            <input type='text' name='name' value={recipe.name || ''} />
            <br/>

            <label>Desc: </label>
            <input type='text' name='desc' value={recipe.description || ''} />
            <br/>

            <label>Ingre: </label>
            {}
        </form>
    );
}

export default RecipeEdit;
