import React, {useEffect, useState} from "react";
import axios from "axios";
import { useLocation, useParams } from "react-router-dom";

const RECIPE_URL = "http://localhost:8000/recipe/recipes/";

function RecipeEdit() {
    let { id } = useParams();
    const recipeToEdit = useLocation().state.recipe;
    const currRecipeId = id;
    const[recipe, setRecipe] = useState();
    const [isLoading, setLoading] = useState(true);

    useEffect(() => {
        if(!recipeToEdit) {
            async function getRecipeInfo() {
                var url = `${RECIPE_URL}${currRecipeId}/`;
                const response = await (axios.get(url));
                setRecipe(response.data);
            };
            getRecipeInfo();
            setLoading(false);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify("Helloo  world"));
    }



    return(
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <h1>Edit recipe: {currRecipeId}</h1>
                </div>

                <label>Id: </label><label readOnly id='textId'>{currRecipeId}</label>
                <br/>

                <label>Name: </label>
                <input type='text' name='name' value={recipeToEdit.name || ''} />
                <br/>
            </form>
        </div>
    );
}

export default RecipeEdit;
