function RecipeEdit(recipe) {

    const recipeObj = recipe.recipe;
    let jsonMap = JSON.stringify([...recipeObj.entries()]);
    let map = new Map(JSON.parse(jsonMap));

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify("Helloo  world"));
    }

    return(
        <form  onSubmit={handleSubmit}>
            <div>
                <h1>Edit recipe: {recipeObj.id}</h1>
            </div>

            {recipeObj.map((element, index) => (
                <div key={index}>
                    <label>{element}</label>
                </div>
            ))}
            <div>
                <button className="button submit" type="submit">Submit</button>
            </div>
        </form>
    );
}

export default RecipeEdit;
