function RecipeEdit(recipe) {

    const recipeObj = recipe.recipe;

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify("Helloo  world"));
    }

    return(
        <form  onSubmit={handleSubmit}>
            <div>
                <h1>Edit recipe: {recipeObj.id}</h1>
            </div>

            <label>Id: {recipeObj.id}</label>
            <br/>

            <label>Name: </label>
            <input type='text' name='name' value={recipeObj.name || ''} />
            <br/>

            <label>Desc: </label>
            <input type='text' name='desc' value={recipeObj.description || ''} />
            <br/>

            <label>Ingr: </label>
            <br/>
            {/* {recipeObj.map((element, index) => (
                <div key={index}>
                    <label>{element}</label>
                </div>
            ))} */}
            <div>
                <button className="button submit" type="submit">Submit</button>
            </div>
        </form>
    );
}

export default RecipeEdit;
