import React, {useEffect, useState} from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

// import { useParams } from 'react-router-dom'

// import RecipeShow from "./RecipeShow";
// import RecipeEdit from "./RecipeEdit";

const RECIPE_URL = "http://localhost:8000/recipe/recipes/";

function Recipe(params) {
    const currRecipeId = params.recipeId;

    const[recipe, setRecipe] = useState();

    useEffect(() => {
        getRecipeInfo();
          // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getRecipeInfo = () => {
        var url = `${RECIPE_URL}${currRecipeId}/`;
        axios.get(url).then((response) => {
            setRecipe(response.data);
        });
    };

    // useNavigate(() => {
    //     navigate("/RecipeEdit", {recipeId: recipeId});
    // }, []);

    let handleEdit = (event) => {
        event.preventDefault();
        // useNavigate();

        alert(JSON.stringify("Helloo  world"));
    }

    return(
        <div>
            <h1>Recipe: {currRecipeId}</h1>
            <pre>
                {JSON.stringify(recipe, null, 4)}
            </pre>
            <div>
                <button className="button submit" type="submit" onClick={handleEdit}>
                <Route exact path='recipe/:id' element={<RecipeEdit />} />
                </button>
            </div>
        </div>
    );
}

export default Recipe;
